// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isUploaderOpen, setIsUploaderOpen] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [files, setFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const chatEndRef = useRef(null);
  const API_URL = 'http://localhost:8000'; // Update this to your API URL

  // Generate a new session on first load
  useEffect(() => {
    fetchSessions();
    generateNewSession();
  }, []);

  // Scroll to bottom whenever messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchSessions = async () => {
    try {
      const response = await fetch(`${API_URL}/sessions`);
      const data = await response.json();
      setSessions(data.sessions || []);
    } catch (error) {
      console.error('Error fetching sessions:', error);
    }
  };

  const generateNewSession = async () => {
    try {
      const response = await fetch(`${API_URL}/generate_session`);
      const data = await response.json();
      setSessionId(data.session_id);
      setMessages([]);
    } catch (error) {
      console.error('Error generating session:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (!input.trim()) return;

    // Check if files have been uploaded for this session
    const userMessage = { text: input, sender: 'user' };
    setMessages([...messages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch(`${API_URL}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          session_id: sessionId,
          message: input,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages([...messages, userMessage, { text: data.error, sender: 'bot', isError: true }]);
      } else if (data.response) {
        // Now storing the response as is, without pre-processing
        setMessages([...messages, userMessage, { text: data.response, sender: 'bot' }]);
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages([...messages, userMessage, { 
        text: 'An error occurred. Please try again later.', 
        sender: 'bot', 
        isError: true 
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleUploadFiles = async (e) => {
    e.preventDefault();

    if (selectedFiles.length === 0) return;

    const formData = new FormData();
    formData.append('session_id', sessionId);

    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append('files', selectedFiles[i]);
    }

    try {
      const response = await fetch(`${API_URL}/upload`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setFiles([...files, ...selectedFiles]);
        setSelectedFiles([]);
        setIsUploaderOpen(false);
        setMessages([...messages, { 
          text: `Successfully uploaded ${selectedFiles.length} file(s).`, 
          sender: 'system' 
        }]);
      }
    } catch (error) {
      console.error('Error uploading files:', error);
      setMessages([...messages, { 
        text: 'Failed to upload files. Please try again.', 
        sender: 'system', 
        isError: true 
      }]);
    }
  };

  const fetchChatHistory = async (sessId) => {
    try {
      setIsLoading(true);
      const response = await fetch(`${API_URL}/chat_history?session_id=${sessId}`);
      const data = await response.json();

      if (data.history) {
        // Convert the API response to our message format
        const formattedMessages = [];
        for (let i = 0; i < data.history.length; i += 2) {
          if (i + 1 < data.history.length) {
            formattedMessages.push({ 
              text: data.history[i].content, 
              sender: 'user' 
            });
            formattedMessages.push({ 
              text: data.history[i + 1].content, 
              sender: 'bot' 
            });
          }
        }

        setMessages(formattedMessages);
      } else {
        setMessages([]);
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
      setMessages([]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileSelect = (e) => {
    setSelectedFiles(Array.from(e.target.files));
  };

  const switchSession = (id) => {
    setSessionId(id);
    setMessages([]);
    fetchChatHistory(id);
    // In a complete implementation, you would fetch the chat history for this session
  };

  // Function to render message content with Markdown if it's from the bot
  const renderMessage = (message) => {
    if (message.sender === 'bot') {
      return (
        <div className={`message-content ${message.isError ? 'error' : ''}`}>
          <ReactMarkdown>{message.text}</ReactMarkdown>
        </div>
      );
    } else {
      return (
        <div className={`message-content ${message.isError ? 'error' : ''}`}>
          {message.text}
        </div>
      );
    }
  };

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <button className="new-chat-btn" onClick={generateNewSession}>
          New Chat
        </button>
        <div className="sessions-list">
          {sessions.map((session) => (
            <div 
              key={session} 
              className={`session-item ${session === sessionId ? 'active' : ''}`}
              onClick={() => switchSession(session)}
            >
              Session {session.substring(0, 8)}...
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className={`main-content ${isSidebarOpen ? '' : 'sidebar-closed'}`}>
        <div className="chat-header">
          <button 
            className="sidebar-toggle" 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            ☰
          </button>
          <span className="session-id">Session: {sessionId.substring(0, 8)}...</span>
        </div>

        <div className="chat-container">
          <div className="messages-container">
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.sender}`}>
                {renderMessage(message)}
              </div>
            ))}
            {isLoading && (
              <div className="message bot">
                <div className="message-content loading">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>

          {/* File Uploader Dialog */}
          {isUploaderOpen && (
            <div className="uploader-overlay">
              <div className="uploader-container">
                <h3>Upload Files</h3>
                <form onSubmit={handleUploadFiles}>
                  <input 
                    type="file" 
                    multiple 
                    onChange={handleFileSelect} 
                    className="file-input"
                  />
                  <div className="selected-files">
                    {Array.from(selectedFiles).map((file, index) => (
                      <div key={index} className="selected-file">
                        {file.name}
                      </div>
                    ))}
                  </div>
                  <div className="uploader-actions">
                    <button type="button" onClick={() => setIsUploaderOpen(false)}>
                      Cancel
                    </button>
                    <button type="submit" disabled={selectedFiles.length === 0}>
                      Upload
                    </button>
                  </div>
                </form>
              </div>
            </div>
          )}

          {/* Input Area */}
          <div className="input-container">
            <button 
              className="upload-btn" 
              onClick={() => setIsUploaderOpen(true)}
            >
              📎
            </button>
            <form onSubmit={handleSendMessage} className="input-form">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type your message..."
                disabled={isLoading}
                className="message-input"
              />
              <button 
                type="submit" 
                disabled={!input.trim() || isLoading}
                className="send-btn"
              >
                ↵
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
