// src/App.js
import React, { useState, useEffect, useRef } from 'react';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [isUploaderOpen, setIsUploaderOpen] = useState(false);
  const [sessions, setSessions] = useState([]);
  const [files, setFiles] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [editingSessionId, setEditingSessionId] = useState(null);
  const [editingTitle, setEditingTitle] = useState('');
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [regeneratingIndex, setRegeneratingIndex] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [showRegenerateModal, setShowRegenerateModal] = useState(false);
  const [regeneratePrompt, setRegeneratePrompt] = useState('');
  const [currentRegenerateConversation, setCurrentRegenerateConversation] = useState(null);
  const chatEndRef = useRef(null);
  const API_URL = 'http://localhost:8000'; // Update this to your API URL

  // Generate a new session on first load
  useEffect(() => {
    fetchSessions();
    // generateNewSession();
  }, []);

  // Scroll to bottom whenever messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // To load uploaded files when sessionId changes
  useEffect(() => {
    if (sessionId) {
      fetchUploadedFiles(sessionId);
    }
  }, [sessionId]);

  const scrollToBottom = () => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchSessions = async () => {
    try {
      const response = await fetch(`${API_URL}/sessions`);
      const data = await response.json();
      setSessions(data.sessions || []);
    } catch (error) {
      console.error('Error fetching sessions:', error);
    }
  };

  const generateNewSession = async () => {
    try {
      const response = await fetch(`${API_URL}/generate_session`);
      const data = await response.json();
      setSessionId(data.session_id);
      setMessages([]);
      setUploadedFiles([]);

      // Refresh sessions list after creating new session
      setTimeout(fetchSessions, 500);
    } catch (error) {
      console.error('Error generating session:', error);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();

    if (!input.trim()) return;

    const userMessage = { 
      text: input, 
      sender: 'user', 
      id: Date.now() + '_user',
      conversation_id: Date.now() + '_conv'  // Add conversation tracking
    };
    setMessages([...messages, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch(`${API_URL}/chat`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          session_id: sessionId,
          message: input,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages([...messages, userMessage, { 
          text: data.error, 
          sender: 'bot', 
          isError: true,
          id: Date.now() + '_bot_error',
          conversation_id: userMessage.conversation_id
        }]);
      } else if (data.response) {
        setMessages([...messages, userMessage, { 
          text: data.response, 
          sender: 'bot',
          id: Date.now() + '_bot',
          conversation_id: userMessage.conversation_id,
          alternatives: [data.response],
          activeIndex: 0,
          total_alternatives: 1
        }]);

        if (messages.length === 0) {
          setTimeout(fetchSessions, 500);
        }
      }
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages([...messages, userMessage, { 
        text: 'An error occurred. Please try again later.', 
        sender: 'bot', 
        isError: true,
        id: Date.now() + '_bot_error',
        conversation_id: userMessage.conversation_id
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // To refresh the uploaded files list
  const handleUploadFiles = async (e) => {
    e.preventDefault();

    if (selectedFiles.length === 0) return;

    const formData = new FormData();
    formData.append('session_id', sessionId);

    for (let i = 0; i < selectedFiles.length; i++) {
      formData.append('files', selectedFiles[i]);
    }

    try {
      setIsUploading(true);
      const response = await fetch(`${API_URL}/upload`, {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (data.success) {
        setSelectedFiles([]);
        // Refresh uploaded files list
        fetchUploadedFiles(sessionId);
        setMessages([...messages, { 
          text: `Successfully uploaded ${data.uploaded_files?.length || 0} file(s)`, 
          sender: 'system' 
        }]);
      } else {
        const errorFiles = data.failed_files ? data.failed_files.join(', ') : 'some files';
        setMessages([...messages, { 
          text: `Failed to upload ${errorFiles}`, 
          sender: 'system', 
          isError: true 
        }]);
      }
    } catch (error) {
      console.error('Error uploading files:', error);
      setMessages([...messages, { 
        text: 'Failed to upload files. Please try again.', 
        sender: 'system', 
        isError: true 
      }]);
    } finally {
      setIsUploading(false);
    }
  };

  const fetchChatHistory = async (sessId) => {
    try {
      setIsLoading(true);
      const response = await fetch(`${API_URL}/chat_history?session_id=${sessId}`);
      const data = await response.json();

      if (data.history && data.history.length > 0) {
        const formattedMessages = data.history.map(message => ({
          text: message.text,
          sender: message.sender,
          id: message.id,
          conversation_id: message.conversation_id,
          alternatives: message.alternatives || [message.text],
          activeIndex: message.activeIndex || 0,
          total_alternatives: message.total_alternatives || 1
        }));

        setMessages(formattedMessages);
      } else {
        setMessages([]);
      }
    } catch (error) {
      console.error('Error fetching chat history:', error);
      setMessages([]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFileSelect = (e) => {
    setSelectedFiles(Array.from(e.target.files));
  };

  const switchSession = (session) => {
    setSessionId(session.session_id);
    setMessages([]);
    fetchChatHistory(session.session_id);
    fetchUploadedFiles(session.session_id);
  };

  // Fetch uploaded files in each session to show them up later
  const fetchUploadedFiles = async (sessId) => {
    try {
      const response = await fetch(`${API_URL}/uploaded_files?session_id=${sessId}`);
      const data = await response.json();
      if (data.files) {
        setUploadedFiles(data.files);
      } else {
        setUploadedFiles([]);
      }
    } catch (error) {
      console.error('Error fetching uploaded files:', error);
      setUploadedFiles([]);
    }
  };

  // Delete Uploaded Files
  const handleDeleteFile = async (fileName) => {
    if (!window.confirm(`Are you sure you want to delete "${fileName}"?`)) {
      return;
    }

    try {
      const response = await fetch(`${API_URL}/delete_file`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          session_id: sessionId,
          filename: fileName
        }),
      });

      const data = await response.json();

      if (data.success) {
        // Refresh the uploaded files list
        fetchUploadedFiles(sessionId);
        setMessages([...messages, { 
          text: `Successfully deleted "${fileName}".`, 
          sender: 'system' 
        }]);
      } else {
        setMessages([...messages, { 
          text: `Failed to delete "${fileName}". ${data.error || ''}`, 
          sender: 'system', 
          isError: true 
        }]);
      }
    } catch (error) {
      console.error('Error deleting file:', error);
      setMessages([...messages, { 
        text: `Failed to delete "${fileName}". Please try again.`, 
        sender: 'system', 
        isError: true 
      }]);
    }
  };

  // Edit the chat name (or session name)
  const startEditingTitle = (session) => {
    setEditingSessionId(session.session_id);
    setEditingTitle(session.title);
  };

  // Save the session name
  const saveTitle = async (sessionId) => {
    try {
      const response = await fetch(`${API_URL}/update_session_title?session_id=${sessionId}&new_title=${encodeURIComponent(editingTitle)}`, {
        method: 'PUT',
      });

      if (response.ok) {
        fetchSessions(); // Refresh the sessions list
        setEditingSessionId(null);
        setEditingTitle('');
      }
    } catch (error) {
      console.error('Error updating title:', error);
    }
  };

  const cancelEditing = () => {
    setEditingSessionId(null);
    setEditingTitle('');
  };

  const deleteSession = async (sessionIdToDelete) => {
    if (window.confirm('Are you sure you want to delete this session?')) {
      try {
        const response = await fetch(`${API_URL}/delete_session?session_id=${sessionIdToDelete}`, {
          method: 'DELETE',
        });

        if (response.ok) {
          fetchSessions(); // Refresh the sessions list
          // If the deleted session was the current one, create a new session
          if (sessionIdToDelete === sessionId) {
            generateNewSession();
          }
        }
      } catch (error) {
        console.error('Error deleting session:', error);
      }
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    } catch {
      return '';
    }
  };

  // Regenerate response
  const handleRegenerate = async (conversationId, customPrompt = '') => {
    setRegeneratingIndex(conversationId);

    try {
      const response = await fetch(`${API_URL}/regenerate`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          session_id: sessionId,
          conversation_id: conversationId,
          regeneration_prompt: customPrompt || null
        })
      });

      const data = await response.json();

      if (data.response) {
        // Update messages with new alternative
        const newMessages = [...messages];
        const botMessageIndex = newMessages.findIndex(msg => 
          msg.conversation_id === conversationId && msg.sender === 'bot'
        );

        if (botMessageIndex !== -1) {
          const botMessage = newMessages[botMessageIndex];
          if (!botMessage.alternatives) {
            botMessage.alternatives = [botMessage.text];
          }

          botMessage.alternatives.push(data.response);
          botMessage.activeIndex = botMessage.alternatives.length - 1;
          botMessage.text = data.response;
          botMessage.total_alternatives = botMessage.alternatives.length;

          setMessages(newMessages);
        }
      }
    } catch (error) {
      console.error('Error regenerating response:', error);
    } finally {
      setRegeneratingIndex(null);
      setShowRegenerateModal(false);
    }
  };

  // function to open regenerate modal
  const openRegenerateModal = (conversationId) => {
    setCurrentRegenerateConversation(conversationId);
    setRegeneratePrompt('');
    setShowRegenerateModal(true);
  };

  // Move through responses
  const navigateAlternatives = (messageIndex, direction) => {
    const newMessages = [...messages];
    const message = newMessages[messageIndex];

    if (!message.alternatives || message.alternatives.length <= 1) return;

    let newIndex = message.activeIndex + direction;

    // Wrap around
    if (newIndex < 0) {
      newIndex = message.alternatives.length - 1;
    } else if (newIndex >= message.alternatives.length) {
      newIndex = 0;
    }

    message.activeIndex = newIndex;
    message.text = message.alternatives[newIndex];

    setMessages(newMessages);
  };

  // Function to render message content with Markdown if it's from the bot
  const renderMessage = (message, index) => {
    if (message.sender === 'bot') {
      const hasAlternatives = message.alternatives && message.alternatives.length > 1;
      const currentIndex = message.activeIndex || 0;
      const totalAlternatives = message.alternatives ? message.alternatives.length : 1;

      return (
        <div className="bot-message-container">
          <div className={`message-content ${message.isError ? 'error' : ''}`}>
            <ReactMarkdown>{message.text}</ReactMarkdown>
          </div>
          <div className="bot-message-controls">
            {hasAlternatives && (
              <div className="alternatives-navigation">
                <button 
                  className="nav-btn"
                  onClick={() => navigateAlternatives(index, -1)}
                  title="Previous alternative"
                >
                  ←
                </button>
                <span className="alternatives-counter">
                  {currentIndex + 1} / {totalAlternatives}
                </span>
                <button 
                  className="nav-btn"
                  onClick={() => navigateAlternatives(index, 1)}
                  title="Next alternative"
                >
                  →
                </button>
              </div>
            )}
            <button 
              className="regenerate-btn"
              onClick={() => handleRegenerate(index)}
              disabled={regeneratingIndex === index}
              title="Regenerate response"
            >
              {regeneratingIndex === index ? '⟳' : '🔄'}
            </button>
          </div>
        </div>
      );
    } else {
      return (
        <div className={`message-content ${message.isError ? 'error' : ''}`}>
          {message.text}
        </div>
      );
    }
  };

  return (
    <div className="app-container">
      {/* Sidebar */}
      <div className={`sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
        <button className="new-chat-btn" onClick={generateNewSession}>
          New Chat
        </button>
        <div className="sessions-list">
          {sessions.map((session) => (
            <div 
              key={session.session_id} 
              className={`session-item ${session.session_id === sessionId ? 'active' : ''}`}
            >
              {editingSessionId === session.session_id ? (
                <div className="session-edit" onClick={(e) => e.stopPropagation()}>
                  <input
                    type="text"
                    value={editingTitle}
                    onChange={(e) => setEditingTitle(e.target.value)}
                    onKeyDown={(e) => {
                      e.stopPropagation();
                      if (e.key === 'Enter') {
                        saveTitle(session.session_id);
                      } else if (e.key === 'Escape') {
                        cancelEditing();
                      }
                    }}
                    onBlur={() => cancelEditing()}
                    className="session-title-input"
                    autoFocus
                  />
                  <div className="session-edit-buttons">
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        saveTitle(session.session_id);
                      }}
                    >
                      ✓
                    </button>
                    <button 
                      onClick={(e) => {
                        e.stopPropagation();
                        cancelEditing();
                      }}
                    >
                      ✕
                    </button>
                  </div>
                </div>
              ) : (
                <div 
                  className="session-content"
                  onClick={() => switchSession(session)}
                >
                  <div className="session-title">{session.title}</div>
                  <div className="session-date">{formatDate(session.last_updated)}</div>
                  <div className="session-actions">
                    <button 
                      className="session-action-btn edit-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        startEditingTitle(session);
                      }}
                    >
                      ✏️
                    </button>
                    <button 
                      className="session-action-btn delete-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        deleteSession(session.session_id);
                      }}
                    >
                      🗑️
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className={`main-content ${isSidebarOpen ? '' : 'sidebar-closed'}`}>
        <div className="chat-header">
          <button 
            className="sidebar-toggle" 
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            ☰
          </button>
          <span className="session-id">
            {sessions.find(s => s.session_id === sessionId)?.title || `Session: ${sessionId?.substring(0, 8) || 'Loading'}...`}
          </span>
        </div>

        <div className="chat-container">
          <div className="messages-container">
            {messages.map((message, index) => (
              <div key={index} className={`message ${message.sender}`}>
                {renderMessage(message, index)}
              </div>
            ))}
            {isLoading && (
              <div className="message bot">
                <div className="message-content loading">
                  <div className="typing-indicator">
                    <span></span>
                    <span></span>
                    <span></span>
                  </div>
                </div>
              </div>
            )}
            <div ref={chatEndRef} />
          </div>

          {/* File Uploader Dialog */}
          {isUploaderOpen && (
            <div className="uploader-overlay">
              <div className="uploader-container">
                <div className="uploader-header">
                  <h3>Manage Files for Session</h3>
                  <button 
                    className="close-btn"
                    onClick={() => setIsUploaderOpen(false)}
                  >
                    ×
                  </button>
                </div>

                {/* Uploaded Files Section */}
                <div className="uploaded-files-section">
                  <h4>Uploaded Files ({uploadedFiles.length})</h4>
                  <div className="uploaded-files-list">
                    {uploadedFiles.length > 0 ? (
                      uploadedFiles.map((file, index) => (
                        <div key={index} className="uploaded-file-item">
                          <span className="file-name">{file}</span>
                          <button 
                            className="delete-file-btn"
                            onClick={() => handleDeleteFile(file)}
                            title="Delete file"
                          >
                            🗑️
                          </button>
                        </div>
                      ))
                    ) : (
                      <div className="no-files-message">
                        No files uploaded for this session yet.
                      </div>
                    )}
                  </div>
                </div>

                {/* Upload New Files Section */}
                <div className="upload-new-section">
                  <h4>Upload New Files</h4>
                  <form onSubmit={handleUploadFiles}>
                    <input 
                      type="file" 
                      multiple 
                      onChange={handleFileSelect} 
                      className="file-input"
                    />
                    <div className="selected-files">
                      {Array.from(selectedFiles).map((file, index) => (
                        <div key={index} className="selected-file">
                          <span>{file.name}</span>
                          <button
                            type="button"
                            onClick={() => {
                              const newFiles = Array.from(selectedFiles).filter((_, i) => i !== index);
                              setSelectedFiles(newFiles);
                            }}
                            className="remove-selected-btn"
                          >
                            ×
                          </button>
                        </div>
                      ))}
                    </div>
                    <div className="uploader-actions">
                      <button type="button" onClick={() => setIsUploaderOpen(false)}>
                        Close
                      </button>
                      <button type="submit" disabled={selectedFiles.length === 0}>
                        Upload ({selectedFiles.length})
                      </button>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          )}

          {/* Input Area */}
          <div className="input-container">
            <button 
              className="upload-btn" 
              onClick={() => setIsUploaderOpen(true)}
            >
              📎
            </button>
            <form onSubmit={handleSendMessage} className="input-form">
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type your message..."
                disabled={isLoading}
                className="message-input"
              />
              <button 
                type="submit" 
                disabled={!input.trim() || isLoading}
                className="send-btn"
              >
                ↵
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
